function resizeGridItem(a){grid=document.getElementsByClassName("grid")[0];rowHeight=parseInt(window.getComputedStyle(grid).getPropertyValue("grid-auto-rows"));rowGap=parseInt(window.getComputedStyle(grid).getPropertyValue("grid-row-gap"));rowSpan=Math.ceil((a.querySelector(".content").getBoundingClientRect().height+rowGap)/(rowHeight+rowGap));a.style.gridRowEnd="span "+rowSpan}function resizeAllGridItems(){allItems=document.getElementsByClassName("item");for(x=0;x<allItems.length;x++){resizeGridItem(allItems[x])}}function resizeInstance(a){item=a.elements[0];resizeGridItem(item)}window.onload=resizeAllGridItems();window.addEventListener("resize",resizeAllGridItems);